import {
    FETCH_PRODUCTS_START,
    FETCH_PRODUCTS_SUCCESS,
    FETCH_PRODUCTS_FAILURE,
    FETCH_PRODUCT_BY_ID_START,
    FETCH_PRODUCT_BY_ID_SUCCESS,
    FETCH_PRODUCT_BY_ID_FAILURE,
    ADD_PRODUCT_TO_BASKET,
    SEARCH_PRODUCT
} from "../actionTypes";
import {
    fetchProducts as fetchProductsApi,
    fetchProductById as fetchProductByIdApi
} from 'api';

export const fetchProducts = () => async dispatch => {
    dispatch({
        type: FETCH_PRODUCTS_START
    })
    try{
        const products = await fetchProductsApi();
        dispatch({
            type: FETCH_PRODUCTS_SUCCESS,
            payload: products
        })
    }catch (err) {
        dispatch({
            type: FETCH_PRODUCTS_FAILURE,
            payload: err,
            error: true
        })
    }
}

export const fetchProductById = id => async (dispatch) => {
    dispatch({
        type: FETCH_PRODUCT_BY_ID_START
    });
    try{
        const product = await fetchProductByIdApi(id);
        dispatch({
            type: FETCH_PRODUCT_BY_ID_SUCCESS,
            payload: product
        })

    } catch (err) {
        dispatch({
            type: FETCH_PRODUCT_BY_ID_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const addProductToBasket = id => dispatch =>{
    dispatch({
        type: ADD_PRODUCT_TO_BASKET,
        payload: id
    })
};

export const searchProduct = text => dispatch =>{
    dispatch({
        type: SEARCH_PRODUCT,
        payload: text
    })
};
