import request from 'superagent';
import {reject} from "ramda";

export const fetchProducts = async () => {
    const {body} = await request.get('http://localhost:8080/rest/products');
    return body;
}

export const fetchProductById = async (id) => {
    const {body} = await request.get(`http://localhost:8080/rest/admin/products/${id}`);
    return body;
}