import React, {Component} from "react";
import {Link} from "react-router-dom";
import {connect} from 'react-redux';

import {getTotalBasketCount, getTotalBasketPrice } from 'selectors';


class BasketCart extends Component{

    constructor(props) {
        super(props);
    }

    render() {
        const {totalBasketCount, totalPrice} = this.props;

        return (
            <div>
                <div className="topbar-cart">
                    <a href="#" className="icon-cart"
                    >
                        <i className="icon-basket f-15"></i>
                        <span className="count cart-count">{totalBasketCount}</span>
                    </a>
                </div>
            </div>
        )
    }
};

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)

    }
};

export default connect(mapStateToProps, null)(BasketCart);

