import React, {useContext} from "react";
import {Link} from "react-router-dom";

import Search from "components/search";
import BasketCart from "components/basket";
import {CurrentUserContext} from "contexts/currentUser";

/**
 * Компонент над баннером
 */
const Topbar = () => {
    const [currentUserState] = useContext(CurrentUserContext)

    return(
        <div className="topbar">
            <div className="container container-40">
                <div className="topbar-left">
                    <div className="topbar-option">
                        {currentUserState.isLoggedIn && (<div className="topbar-account">
                            <Link to="/profile"><i className="icon-user f-15"></i> {currentUserState.currentUser.username}</Link>
                        </div>)}

                    </div>
                </div>
                <div className="logo hidden-xs hidden-sm">
                    <a href="/" title="home-logo"><img src="/img/main/aqualogo.png" alt="logo" className="img-responsive"/></a>
                </div>
                <div className="topbar-right">
                    <div className="topbar-option">
                        <Search />
                        <BasketCart />
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Topbar